mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM review_likes
    WHERE user_id = #{userId}
      AND review_id = #{reviewId}
)

mapper.update:
UPDATE book_review
SET likes_count = likes_count + 1
WHERE review_id = #{reviewId}

mapper.delete:
DELETE FROM review_likes
WHERE user_id = #{userId}
  AND review_id = #{reviewId}

mapper.update:
UPDATE book_review
SET likes_count = likes_count - 1
WHERE review_id = #{reviewId}

mapper.select:
SELECT COUNT(*)
FROM review_likes
WHERE review_id = #{reviewId}
